package ch.bbw.zork;public class CommandWords {		private static final String validCommands[] = {		"go", "quit", "help", "map", "take", "items"	};	public boolean isCommand(String aString) {		for (int i=0; i < validCommands.length; i++) {			if (validCommands[i].equals(aString))				return true;		}		return false;	}	public String showAll() {		String outputStr= "";		for (int i=0; i < validCommands.length; i++) {			outputStr += validCommands[i] + "  ";		}		return outputStr;	}}